        self.bsc = 'https://bsc-dataseed.binance.org/'
        self.web3 = Web3(Web3.HTTPProvider(self.bsc))

        # user interface app
        self.entry_app = entry_app
        self.entry_app.update_listbox("web3 https provider connect")


